import React, {Fragment, useState} from 'react';


export const RegistrationForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        telephone: '',
        message: ''
    });

    const { name, email, telephone, message } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value});

    const onSubmit = e => {
        e.preventDefault();
        console.log(formData);
    }

    return (
        <Fragment>
            <form onSubmit={onSubmit}>
                <input type='text' placeholder='Name' name='name' value={name} onChange={onChange} required />
                <input type='text' placeholder='Email' name='email' value={email} onChange={e => onChange(e)} required />
                <input type='text' placeholder='Phone #' name='telephone' value={telephone} onChange={e => onChange(e)} />
                <input type='text' placeholder='Message' name='message' value={message} onChange={e => onChange(e)} required />
                <button>Go</button>
            </form>
        </Fragment>
    )
}
