import React, {Fragment, useState} from 'react';
import axios from "axios";


export const RegistrationForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        telephone: '',
        message: ''
    });

    const { name, email, telephone, message } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value});

    const onSubmit = e => {
        e.preventDefault();
        const newRegistration ={
            name,
            email,
            telephone,
            message
        };

        try {
            const config = {
                headers:{
                    'Content-Type': 'application/json'
                }
            };

            const body = JSON.stringify(newRegistration);

            const res = axios.post('/api/registration', body, config);
            console.log(res.data);
        } catch (err) {
            console.error(err.response.data)
        }

        console.log(formData);
    }

    return (
        <Fragment>
            <form onSubmit={onSubmit}>
                <input type='text' placeholder='Name' name='name' value={name} onChange={onChange} required />
                <input type='text' placeholder='Email' name='email' value={email} onChange={onChange} required />
                <input type='text' placeholder='Phone #' name='telephone' value={telephone} onChange={onChange} />
                <input type='text' placeholder='Message' name='message' value={message} onChange={onChange} required />
                <button type="submit" value="Register">Go</button>
            </form>
        </Fragment>
    )
}
