import React, { useState } from "react";
import "./StarRating";

export const StarRating = () => {
    const [rating, setRating] = useState(0);

    return (
      <div className="star-rating">
        {[...Array(5)].map((star) => {    
          index += 1;    
          return (  
            <button
            type="button"
            key={index}
            className={index <= rating ? "on" : "off"}
            onClick={() => setRating(index)}
          >       
                <span className="star">&#9733;</span>      
            </button>  
          );
        })}
      </div>
    );
  };
